def isSubsequence( s, t):
    counter = 0
    lens = len(s)
    if lens == 0: return False
    for each in t:
        if counter < lens and s[counter] == each:
            counter += 1
    return False if counter <= lens-1 else True

s = "leeeeetcode"
t = ""
print( isSubsequence(s, t) ) 
            